const Contador = (params) => {
  const [stateAumento, updateStateAumento] = useState(params.num);

  return (
    <>
      <span>{stateAumento}</span>
      <button
        onClick={() => {
          return updateStateAumento(stateAumento + 1);
        }}
      >
        Aumentar
      </button>
      <button
        onClick={() => {
          return updateStateAumento(stateAumento - 1);
        }}
      >
        Desaumentar
      </button>
    </>
  );
};

export default Contador;
